package Exercicios;

/*Escreva um algoritmo que exiba na tela os valores impares no intervalo 0 a 100.*/

public class Exercicio_2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for(int i = 0; i<100; i++) {
			if(i%2!=0)
			System.out.println(i);
		}
		
		
		
		
	}

}
